<template>
  <div class="home">
      后台首页
     <hr>
     {{list}}
    <ul class="music">
      <li v-for="(v,k) in list" :key="'list'+k">
        <p>{{v.text}}</p>
        <!--隐藏，就不写controls,preload 预加数，提高速度
        dom 03 :ref="el =>{domList[k] = el}" k 是当前循环的索引
        -->
        <audio :src="v.url"  :id="'m'+k" preload="auto" :ref="el =>{domList[k] = el}"></audio>
        <img :src="v.status == true ? play : close" @click="play_audio(k)">
      </li>
    </ul>

    <hr>
    <!-- <el-dropdown @command="handleMenu">
      <span class="el-dropdown-link">
        ...
        <el-icon class="el-icon--right">
          <arrow-down />
        </el-icon>
      </span>
      <template #dropdown>
        <el-dropdown-menu>
          <el-dropdown-item command="1">详情</el-dropdown-item>
          <el-dropdown-item command="2">删除</el-dropdown-item>
        </el-dropdown-menu>
      </template>
    </el-dropdown> -->

   <ul>
     <li v-for="(v,k) in picList" :key="'list_'+k">
       <p>{{v.text}}</p>
        <el-dropdown>
          <span class="el-dropdown-link">
            管理...
            <el-icon class="el-icon--right">
              <arrow-down />
            </el-icon>
          </span>
          <template #dropdown>
            <el-dropdown-menu>
              <el-dropdown-item >详情</el-dropdown-item>
              <el-dropdown-item @click="delItem(k)">删除</el-dropdown-item>
            </el-dropdown-menu>
          </template>
        </el-dropdown>
     </li>
   </ul>


  </div>
</template>

<script lang="ts" setup>
  import {reactive,ref} from 'vue'
  let play = ref("data:image/png;base64,")
  let close = ref("data:image/png;base64,")

  let list = reactive([
    {
      text:"音乐1",
      url:"http://downsc.chinaz.net/Files/DownLoad/sound1/201906/11582.mp3",
      status:false
    },
    {
      text:"音乐2",
      url:"http://downsc.chinaz.net/files/download/sound1/201206/1638.mp3",
      status:false
    }
  ])

  // dom 01
  const domList = ref({})

  const play_audio = (k)=>{
    console.log(k)
    list[k].status = !list[k].status
 
    console.log(domList.value)
    
    // let m =  document.querySelector("#m"+k);
    // dom 02
    let m = domList.value[k]
    console.log(m)

    if(m.paused){
      m.load();//解决只播放一次的问题
      m.play();
    }else{
      m.pause()
    }
  }

  // 列表 + 下拉菜单删除 
  let picList = reactive([
    {
      id:1,
      text:"p1"
    },
    {
      id:1,
      text:"p2"
    }
  ])
  const delItem = (val)=>{
    picList.splice(val,1)
  }
</script>

<style scoped>
  .music li{
    list-style: none;
  }

  .music li img{
    width: 50px;
  }

  .el-dropdown-link {
    cursor: pointer;
    color: #409EFF;
  }
  .el-icon-arrow-down {
    font-size: 12px;
  }
