<template>
  <div class="home">
      后台首页
     <hr>
     {{list}}
    <ul class="music">
      <li v-for="(v,k) in list" :key="'list'+k">
        <p>{{v.text}}</p>
        <!--隐藏，就不写controls,preload 预加数，提高速度-->
        <audio :src="v.url"  :id="'m'+k" preload="auto"></audio>
        <img :src="v.status == true ? play : close" @click="play_audio(k)">
      </li>
    </ul>

  </div>
</template>

<script lang="ts" setup>
  import {reactive,ref} from 'vue'
  // 关：data:image/png;base64,
  // 开：data:image/png;base64,
  let play = ref("data:image/png;base64,")
  let close = ref("data:image/png;base64,")

  let list = reactive([
    {
      text:"音乐1",
      url:"http://downsc.chinaz.net/Files/DownLoad/sound1/201906/11582.mp3",
      status:false
    },
    {
      text:"音乐2",
      url:"http://downsc.chinaz.net/files/download/sound1/201206/1638.mp3",
      status:false
    }
  ])

  // https://blog.csdn.net/weixin_43233914/article/details/108776124
  const play_audio = (k)=>{
    console.log(k)
    list[k].status = !list[k].status
 
    let m =  document.querySelector("#m"+k);
    if(m.paused){
      m.load();//解决只播放一次的问题
      m.play();
    }else{
      m.pause()
    }
  }

</script>


<style scoped>
  .music li{
    list-style: none;
  }

  .music li img{
    width: 50px;
  }
</style>